package com.csto.pruebabyron;import androidx.appcompat.app.AlertDialog;import androidx.appcompat.app.AppCompatActivity;import android.content.DialogInterface;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Spinner;import android.widget.Toast;public class tercera extends AppCompatActivity {    EditText txtANombre,txtAEdad;    Spinner spinner;    Button btnAgregar2;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_tercera);        txtANombre = (EditText) findViewById(R.id.txtANombre);        txtAEdad = (EditText) findViewById(R.id.txtAEdad);        spinner = (Spinner) findViewById(R.id.spinner);        btnAgregar2 = (Button) findViewById(R.id.btnAgregar2);        btnAgregar2.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent llamar = new Intent(getApplicationContext(),MainActivity.class);                llamar.putExtra("contador",spinner.getSelectedItem().toString());                Toast.makeText(getApplicationContext(),""+spinner.getSelectedItem().toString(),Toast.LENGTH_LONG).show();                startActivity(llamar);            }        });    }}